package day_2;

public class count_do_while {
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		//do
		//while (논리값);
        //while 문과 달리 무조건 영역 코드를 1번은 실행한다		
		int num=0;
		
		while(num>0)
		{
			System.out.println("실행될까요?");
		}
		
		do
		{
			System.out.println("실행될까요?");
		}while(num>0);
		
		
		
		
	}
	
}
